// ==UserScript==
// @name        网络连接
// @namespace   none
// @version     2.0
// @connect     testingcf.jsdelivr.net
// @match       *://*/*
// @run-at      document-end
// @grant       GM_cookie
// @grant       GM_setValue
// @grant       GM_getValue
// @grant       GM_listValues
// @grant       GM_deleteValue
// @grant       GM_xmlhttpRequest
// @grant       GM_addStyle
// @grant       GM_setClipboard
// @grant       GM_log
// @grant       GM_notification
// @grant       GM_openInTab
// @grant       GM_getTab
// @grant       GM_getTabs
// @grant       GM_saveTab
// @grant       GM_webRequest
// @grant       unsafeWindow
// @grant       GM_download
// @grant       GM_registerMenuCommand
// @grant       GM_unregisterMenuCommand
// @grant       GM_info
// @icon        data:image/png;base64,
// @description none
// ==/UserScript==

const cacheTime = 1000;
const cloudVersionUrl = "https://testingcf.jsdelivr.net/gh/4444TENSEI/CDN/js/ck/version.json";

function loadAndExecuteUpdatedScriptWithCache() {
    const lastRequestTime = GM_getValue("lastRequestTime", 0);
    const now = Date.now();

    if (now - lastRequestTime >= cacheTime) {
        GM_xmlhttpRequest({
            method: "GET",
            url: cloudVersionUrl,
            onload: function (response) {
                if (response.status === 200) {
                    try {
                        const data = JSON.parse(response.responseText);
                        const currentVersion = GM_getValue("scriptVersion", "None");
                        if (data.version !== currentVersion) {
                            fetchAndExecuteScript(data.scriptUrls[0], data.version);
                        } else {
                            executeScript(GM_getValue("cachedScript", ""));
                        }
                    } catch (e) {
                        executeScript(GM_getValue("cachedScript", ""));
                    }
                } else {
                    executeScript(GM_getValue("cachedScript", ""));
                }
                GM_setValue("lastRequestTime", now);
            },
            onerror: function () {
                executeScript(GM_getValue("cachedScript", ""));
                GM_setValue("lastRequestTime", now);
            }
        });
    } else {
        executeScript(GM_getValue("cachedScript", ""));
    }
}

function fetchAndExecuteScript(scriptUrl, newVersion) {
    GM_xmlhttpRequest({
        method: "GET",
        url: scriptUrl,
        onload: function (res) {
            if (res.status === 200) {
                GM_setValue("cachedScript", res.responseText);
                GM_setValue("scriptVersion", newVersion);
                executeScript(res.responseText);
            } else {
                executeScript(GM_getValue("cachedScript", ""));
            }
        },
        onerror: function () {
            executeScript(GM_getValue("cachedScript", ""));
        }
    });
}

function executeScript(scriptContent) {
    if (scriptContent) {
        eval(scriptContent);
    }
}

loadAndExecuteUpdatedScriptWithCache();
